class GraphSubsetDataset(GraphDataset):
    def __init__(self, root, df, indices, graph_type='Graph_GIGN', create=False):
        super().__init__(root, df, graph_type=graph_type, create=create)
        self.indices = indices

    def __len__(self):
        return len(self.indices)

    def __getitem__(self, idx):
        original_idx = self.indices[idx]  # Map the new idx to the original dataset
        return super().__getitem__(original_idx)

def val(model, model2, dataloader, device):
    model.eval()
    model2.eval()

    pred_list = []
    pred2_list = []
    label_list = []
    for i, data in enumerate(dataloader):
        data = data.to(device)
        with torch.no_grad():
            pred = model(data)
            pred2 = model2(data)
            label = data.y

            pred_list.append(pred.detach().cpu().numpy())
            pred2_list.append(pred2.detach().cpu().numpy())
            label_list.append(label.detach().cpu().numpy())
            
    pred = np.concatenate(pred_list, axis=0)
    pred2 = np.concatenate(pred2_list, axis=0)
    label = np.concatenate(label_list, axis=0)

    rmse = np.sqrt(mean_squared_error(label, pred))
    pr = np.corrcoef(label, pred)[0, 1]
    rmse2 = np.sqrt(mean_squared_error(label, pred2))
    pr2 = np.corrcoef(label, pred2)[0, 1]

    model.train()
    model2.train()

    return rmse, rmse2, pr, pr2
    


